import sys
import os

# Add the parent directory to the path
sys.path.append(os.path.dirname(os.path.dirname(os.path.abspath(__file__))))

from src.plugin_interface import BasePlugin, PluginMetadata, ToolDefinition

class MyCustomPlugin(BasePlugin):
    @property
    def metadata(self) -> PluginMetadata:
        return PluginMetadata(
            name="my_custom_plugin",
            version="1.0.0",
            description="My custom plugin description",
            author="Your Name"
        )

    def get_tools(self) -> List[ToolDefinition]:
        return [
            ToolDefinition(
                name="my_tool",
                description="Description of my tool",
                handler=self.my_tool_handler
            )
        ]

    def my_tool_handler(self, param1: str) -> str:
        """Handle the custom tool"""
        return f"Processed: {param1}"

    def _setup(self):
        """Called when plugin is initialized"""
        print("My plugin is starting up!")

    def _teardown(self):
        """Called when plugin is cleaned up"""
        print("My plugin is shutting down!")

